import { useEffect, useState } from "react";
import "./Login.css";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
function Login() {
  const navigate = useNavigate();

  useEffect(() => {
    let login = localStorage.getItem("accessToken");
    if (login) {
      navigate("/Home");
    }
  });
  // const validateLogin = () => {
  //   // let userEmail = email.valueOf;
  //   // const emailRegx = /^[a-z0-9._%+-]+@[a-z]+\.[a-z]{1,3}$/;
  // };

  function LoginFun(accessToken) {
    console.log(accessToken);
    localStorage.setItem("accessToken", accessToken);
    navigate("/Home");
  }

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const loginUser = async () => {
    // validateLogin();

    try {
      let bodyContent = {
        email: email,
        password: password,
      };

      let reqOptions = {
        url: "https://4zjnw94s-3131.inc1.devtunnels.ms/auth/login",
        method: "POST",
        data: bodyContent,
      };

      let { data } = await axios.request(reqOptions);
      if (data.success) {
        LoginFun(data.accessToken);
      } else {
        alert(data.message);
      }
    } catch (e) {
      console.log("error", e);
      alert(e.response.data.errors);
    }
  };

  return (
    <div className="Body-1">
      <form className="form-1">
        <div className="title-1">
          <h1>Sign in to your account</h1>
          <p>to add your task✌️</p>
        </div>
        <div className="form-content">
          <label htmlFor="first" className="req">
            Email Address
          </label>
          <input
            type="email"
            id="email"
            name="email"
            className="input-1"
            value={email}
            onChange={(e) => {
              setEmail(e.target.value);
            }}
            required
          />
          <br></br>
          <br></br>
          <label htmlFor="password" className="req">
            Password
          </label>
          <input
            type="text"
            id="password"
            name="password"
            className="input-1"
            value={password}
            onChange={(e) => {
              setPassword(e.target.value);
            }}
            required
          />
          <br></br>
          <br></br>
          <div style={{ display: "flex", flexDirection: "row" }}>
            <input
              type="checkbox"
              style={{ width: 20, height: 20, marginRight: 10 }}
            />
            <label>Remember Me</label>
          </div>
          <br></br>

          <div className="button-cs">
            <button type="submit" className="btn1" onClick={loginUser}>
              Submit
            </button>
          </div>
          <div className="linkk">
            <p>
              New here?<Link to="/SignUp">Register</Link>
            </p>
            <br></br>
          </div>
        </div>
      </form>
    </div>
  );
}
export default Login;
