const sortfun = (data, key, mode) => {
  console.log("data======>>>", data);
  return [...data].sort((i, j) => {
    if (mode == "asc") {
      if (i[key].toLowerCase() < j[key].toLowerCase()) {
        return 1;
      } else {
        return -1;
      }
    } else if (mode == "desc") {
      if (i[key].toLowerCase() > j[key].toLowerCase()) {
        return 1;
      } else {
        return -1;
      }
    }
  });
};

export default sortfun;
