import "./SignUp.css";
import { Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

function SignUp() {
  const navigate = useNavigate();

  useEffect(() => {
    let accessToken = localStorage.getItem("accessToken");
    if (accessToken) {
      navigate("/Home");
    }
  }, []);
  function SignupFun(accessToken) {
    localStorage.setItem("accessToken", accessToken);
    navigate("/Home");
  }
  // const [loading, setLoading] = useState(false);
  // const [first, setFirst] = useState("");
  // const [email, setEmail] = useState("");
  // const [password, setPassword] = useState("");

  const saveUser = async () => {
    try {
      setLoading(true);
      let formdata = new FormData();
      formdata.append("name", first);
      formdata.append("email", email);
      formdata.append("password", password);

      let bodyContent = formdata;

      let reqOptions = {
        url: "https://4zjnw94s-3131.inc1.devtunnels.ms/auth/signup",
        method: "POST",
        data: bodyContent,
      };

      let { data } = await axios.request(reqOptions);
      setLoading(false);
      //console.log(response.data);
      if (data.success) {
        SignupFun(data.accessToken);
      } else {
        alert(data.message);
      }
    } catch (e) {
      console.log(e);
    }
  };
  return (
    <div className="Body-2">
      {loading ? (
        <div className="loader-container">
          <div className="spinner"></div>
        </div>
      ) : (
        <form className="form-2">
          <div className="title-2">
            <h1>Sign Up</h1>
            <p>to make your time productive ✌️</p>
          </div>
          <div className="form-content2">
            <div className="fullname">
              <label htmlFor="firstname" className="req-2">
                First Name
              </label>
              <input
                type="text"
                id="firstname"
                name="first"
                className="input-2"
                value={first}
                onChange={(e) => {
                  setFirst(e.target.value);
                }}
                required
              />

              <label htmlFor="LastName" className="re">
                Last Name
              </label>
              <input
                type="text"
                id="LastName"
                name="last"
                className="input-2"
                // value={last}
              />
            </div>

            <label htmlFor="email-1" className="req-2">
              Email Address
            </label>
            <input
              type="email"
              id="email-1"
              name="email"
              className="input-2"
              value={email}
              onChange={(e) => {
                setEmail(e.target.value);
              }}
              required
            />
            <br></br>
            <br></br>

            <label htmlFor="password-1" className="req-2">
              Password
            </label>
            <input
              type="text"
              id="password-1"
              name="password-1"
              className="input-2"
              value={password}
              onChange={(e) => {
                setPassword(e.target.value);
              }}
              required
            />

            <div className="button-su">
              <button type="button" className="btn-2" onClick={saveUser}>
                Sign Up
              </button>
            </div>
            <div className="link-2">
              <p>
                Already a user?<Link to="/Login">Login</Link>
              </p>
              <br></br>
            </div>
          </div>
        </form>
      )}
    </div>
  );
}
export default SignUp;
