let mode = true;
const sortfun = (data, key) => {
  return [...data].sort((i, j) => {
    if (mode == true) {
      if (i[key].toLowerCase() > j[key].toLowerCase()) {
        mode = false;
        return 1;
      } else {
        mode = false;
        return -1;
      }
    } else {
      if (i[key].toLowerCase() < j[key].toLowerCase()) {
        mode = true;
        return 1;
      } else {
        mode = true;
        return -1;
      }
    }
  });
};

export default sortfun;
