import { createSlice } from "@reduxjs/toolkit";

let todoSlice3 = createSlice({
  name: "mySlice4",
  initialState: {
    title: "",
  },
  reducers: {
    setTitle: (state, action) => {
      state.title = action.payload;
    },
  },
});

export const { setTitle } = todoSlice3.actions;
export default todoSlice3.reducer;
